import React, { useState } from 'react';
import './ConnectionsPage.css';
import baseballImg from '../images/baseball.png';

const ConnectionsPage = () => {
  const initialTerms = ['SWIFT', 'VEHICLE', 'LARGE', 'LEGEND', 'SMALL', 'MEANS', 'MEDIUM', 'MARS', 'OUTSIDE', 'ROOM', 'GRANDE', 'SLIM', 'PROOF', 'STYLES', 'REMOTE', 'CHANNEL'];

  const [terms, setTerms] = useState(initialTerms);
  const [selectedTerms, setSelectedTerms] = useState([]);
  const [mistakes, setMistakes] = useState(4);
  const [submittedSets, setSubmittedSets] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  const handleTermClick = (term) => {
    // Check if the term is already selected; if so, allow it to be deselected.
    if (selectedTerms.includes(term)) {
      setSelectedTerms(selectedTerms.filter(t => t !== term));
      setShowPopup(false);
    } else {
      // Only add the term if fewer than 4 terms are currently selected.
      if (selectedTerms.length < 4) {
        setSelectedTerms([...selectedTerms, term]);
        setShowPopup(false);
      } else {
        // Optionally, you can alert the user that no more than 4 terms can be selected.
        setShowPopup(true);
        setTimeout(() => setShowPopup(false), 2000);
      }
    }
  };

  const handleSubmit = () => {
    // Convert the current set of selected terms to a string for easy comparison
    const currentSetString = [...selectedTerms].sort().join(',');

    // Check if the current set has already been submitted
    if (submittedSets.includes(currentSetString)) {
      // Show popup and set a timer to hide it after 2 seconds
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 2000);
      return; // Prevent further execution
    }

    // Add the current set to the list of submitted sets
    setSubmittedSets([...submittedSets, currentSetString]);

    // Reset the selected terms as before
    setSelectedTerms([]);

    // Decrement the mistake count or handle the correct guess
    setMistakes(mistakes - 1);
  };

  const handleShuffle = () => {
    setTerms(shuffledTermsArray);
    setSelectedTerms([]);
  };

  // A simple shuffle function for the terms
  const shuffleArray = (array) => {
    let currentIndex = array.length,  randomIndex;

    // While there remain elements to shuffle...
    while (currentIndex !== 0) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      // And swap it with the current element.
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array;
  };

  const shuffledTermsArray = shuffleArray([...terms]);

  return (
    <div className="connections-game">
      <h1>Create four groups of four!</h1>
      <div className="terms-grid">
      {showPopup && (
      <div className="popup-message">
        You cannot select more than 4 players.
      </div>
    )}
        {terms.map((term, index) => (
          <div key={index} className={`term-block ${selectedTerms.includes(term) ? 'selected' : ''}`} onClick={() => handleTermClick(term)}>
            {term}
          </div>
        ))}
      </div>
      <div className="mistakes-section">
        <div className="mistakes-indicator">
          <span className="mistakes-text">Mistakes remaining: </span>
          {Array.from({ length: mistakes }, (_, i) => (
          <img key={i} src={baseballImg} alt="Baseball" className="baseball-icon" />
        ))}
        </div>
      </div>
      <div className="game-controls">
        <button onClick={handleShuffle}>Shuffle</button>
        <button onClick={() => setSelectedTerms([])}>Deselect All</button>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default ConnectionsPage;
